import React from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import result from "./data";

class SearchArea extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			departure:"",
			arival:""
		}
	}

	render(){
		return(
		  <Container>
			  <Row>
				  <Col className="d-flex justify-content-center">
				  	<label>
						出発:
						<input type="text" name="departure" onChange={this.handleChange}
						value = {this.state.departure}/>
					</label>
				  </Col>
			  </Row>

			  <Row>
				  <Col className="d-flex justify-content-center">
				  	<label>
						到着:
						<input type="text" name="arival" onChange={this.handleChange}
						value = {this.state.arival}/>
					</label>
				  </Col>
			  </Row>

			  <Row>
				  <Col className="d-flex justify-content-center">
				  	<button onClick={this.handleSubmit}>
					検索
					</button>
				  </Col>
			  </Row>
			  <div>
				<p>出発バス停:{this.state.departure}</p>
				<p>到着バス停:{this.state.arival}</p>
			  </div>


			<br/>
		  </Container>

		)
	}

	handleChange = (event)=>{
		this.setState({[event.target.name]:event.target.value})
	}

	handleSubmit = event =>{
		event.preventDefault();
		this.setState(
				{departure:this.state.departure},
				{arival:this.state.arival}
		);
	}

}
export default SearchArea;
